mapper.select:
SELECT HOUR(create_time) AS time, COUNT(DISTINCT user_id) AS count
FROM mry_member_oper_log

    
        and create_time >= #{beginTime}
    
    
        and create_time  <=  #{endTime}
    

GROUP BY HOUR(create_time)

SELECT DATE_FORMAT(create_time, '%Y-%m-%d') AS time, COUNT(DISTINCT user_id) AS count
FROM mry_member_oper_log

    
        and create_time >= #{beginTime}
    
    
        and create_time  <=  #{endTime}
    

GROUP BY DATE_FORMAT(create_time, '%Y-%m-%d')

SELECT COUNT(*) AS count
FROM mry_member

    delete_flag=0
    
        and birth_date >= #{beginTime}
    
    
        and birth_date  <=  #{endTime}
