select.where:
m.delete_flag = 0
 and user_name like concat('%', #{userName}, '%')
 and phone = #{phone}
 and vip_number = #{vipNumber}
 and skin_type = #{skinType}
 and open_id = #{openId}
 and mini_program_open_id = #{miniProgramOpenId}
 and union_id = #{unionId}

mapper.select:
select distinct m.id, m.user_name, m.phone, m.vip_number, m.birth_date, m.gender, m.address, m.skin_type, m.remark, m.create_time, m.update_time
from mry_member as m
LEFT JOIN  mry_member_device_relation as md  on md.user_id= m.id
LEFT JOIN mry_device d  on md.device_id=d.id
LEFT JOIN mry_product p on d.product_id = p.id

    m.delete_flag = 0
     and m.user_name like concat('%', #{userName}, '%')
     and m.phone = #{phone}
     and m.has_bind_device = #{deviceStatus}
     and m.vip_number = #{vipNumber}
     and d.sn = #{deviceSn}
     and p.model = #{productMode}

 order by m.create_time desc
